import React from "react";

interface btnProps {
  idx: number;
  selectedIdx: number;
  text: string;
  hoverHandler: (idx: number) => void;
}

function Btn(props: btnProps) {
  if (props.selectedIdx === props.idx)
    return (
      <div className="d-flex">
        <svg className="App-btn-marker">
          <path
            fill="None"
            stroke="#FFC000"
            strokeWidth="3px"
            strokeLinejoin="round"
            width={25}
            transform="translate(4,4)"
            d="M 0,0 L 0,24 20,12 Z"
          />
        </svg>
        <p className="App-header-btn App-header-btn-selected">{props.text}</p>
      </div>
    );
  else
    return (
      <p
        className="App-header-btn"
        onMouseEnter={() => props.hoverHandler(props.idx)}>
        {props.text}
      </p>
    );
}

export class HomePage extends React.Component<{}, { btnIdx: number }, any> {
  constructor(props: any) {
    super(props);
    this.state = { btnIdx: 0 };
  }

  handleHover = (idx: number) => {
    this.setState({ btnIdx: idx });
  };

  render() {
    return (
      <header className="App-header">
        <p className="App-title">Gym Battles</p>
        <Btn
          text="Create Session"
          idx={0}
          selectedIdx={this.state.btnIdx}
          hoverHandler={this.handleHover}
        />
        <Btn
          text="Join Session"
          idx={1}
          selectedIdx={this.state.btnIdx}
          hoverHandler={this.handleHover}
        />
      </header>
    );
  }
}
